{
  "name": "Globe Mates",
  "description": "A social and secure meeting app for young travelers to find buddies, join local activities, and share cultural experiences safely.",
  "requestFramePermissions": [
    "geolocation"
  ]
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export enum Gender {
  MALE = 'Male',
  FEMALE = 'Female',
  NON_BINARY = 'Non-Binary',
  OTHER = 'Other'
}

export enum VerificationStatus {
  PENDING = 'Pending',
  VERIFIED = 'Verified',
  REJECTED = 'Rejected'
}

export interface Interest {
  id: string;
  name: string;
}

export interface Goal {
  id: string;
  name: string;
}

export interface UserProfile {
  id: string;
  email: string;
  name: string;
  age: number;
  gender: Gender;
  origin: string;
  languages: string[];
  bio: string;
  interests: string[];
  goals: string[];
  photo?: string;
  verificationStatus: VerificationStatus;
  location: {
    lat: number;
    lng: number;
  };
  rating: number;
  ratingCount: number;
}

export interface Activity {
  id: string;
  creatorId: string;
  title: string;
  type: string;
  description: string;
  dateTime: string;
  locationName: string;
  location: {
    lat: number;
    lng: number;
  };
  participants: string[]; // User IDs
}

export interface Message {
  id: string;
  senderId: string;
  text: string;
  timestamp: string;
}

export interface Conversation {
  id: string;
  participants: string[];
  lastMessage?: string;
  lastMessageTime?: string;
}

export interface AppState {
  currentUser: UserProfile | null;
  users: UserProfile[];
  activities: Activity[];
  conversations: Conversation[];
  messages: Record<string, Message[]>; // conversationId -> messages
}

export interface Filters {
  maxDistance: number;
  minAge: number;
  maxAge: number;
  gender: Gender | 'All';
  interests: string[];
  goals: string[];
}

import React, { useState, useEffect, useMemo } from 'react';
import { HashRouter, Routes, Route, Navigate, Link, useLocation, useNavigate } from 'react-router-dom';
import { 
  Users, 
  MapPin, 
  MessageCircle, 
  User, 
  PlusCircle, 
  Search, 
  ShieldCheck, 
  Settings,
  AlertTriangle,
  X,
  ChevronLeft,
  Send,
  Star,
  CheckCircle,
  Flag
} from 'lucide-react';
import { 
  UserProfile, 
  Gender, 
  VerificationStatus, 
  Activity, 
  Conversation, 
  Message, 
  Filters 
} from './types';
import { 
  MOCK_USERS, 
  MOCK_ACTIVITIES, 
  INTEREST_TAGS, 
  GOAL_TAGS 
} from './constants';

// --- Components ---

const Badge: React.FC<{ children: React.ReactNode; color?: string }> = ({ children, color = 'blue' }) => (
  <span className={`px-2 py-0.5 rounded-full text-xs font-semibold bg-${color}-100 text-${color}-700`}>
    {children}
  </span>
);

const Navbar: React.FC = () => {
  const location = useLocation();
  const navItems = [
    { path: '/explore', icon: Search, label: 'Explore' },
    { path: '/activities', icon: MapPin, label: 'Events' },
    { path: '/chats', icon: MessageCircle, label: 'Chats' },
    { path: '/profile', icon: User, label: 'Me' },
  ];

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-slate-200 px-6 py-2 flex justify-between items-center z-50">
      {navItems.map((item) => (
        <Link 
          key={item.path} 
          to={item.path} 
          className={`flex flex-col items-center gap-1 ${location.pathname === item.path ? 'text-blue-600' : 'text-slate-400'}`}
        >
          <item.icon size={22} />
          <span className="text-[10px] font-medium">{item.label}</span>
        </Link>
      ))}
    </nav>
  );
};

// --- Utils ---
const calculateDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
  const R = 6371; // km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
    Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// --- Views ---

const Explore: React.FC<{ users: UserProfile[], currentUserId: string, filters: Filters, onFilterChange: (f: Filters) => void }> = ({ users, currentUserId, filters, onFilterChange }) => {
  const [showFilters, setShowFilters] = useState(false);
  const currentUser = users.find(u => u.id === currentUserId);

  const filteredUsers = useMemo(() => {
    return users.filter(user => {
      if (user.id === currentUserId) return false;

      // Strict filters
      if (filters.gender !== 'All' && user.gender !== filters.gender) return false;
      if (user.age < filters.minAge || user.age > filters.maxAge) return false;

      if (currentUser) {
        const dist = calculateDistance(currentUser.location.lat, currentUser.location.lng, user.location.lat, user.location.lng);
        if (dist > filters.maxDistance) return false;
      }

      if (filters.interests.length > 0) {
        if (!filters.interests.some(interest => user.interests.includes(interest))) return false;
      }

      if (filters.goals.length > 0) {
        if (!filters.goals.some(goal => user.goals.includes(goal))) return false;
      }

      return true;
    });
  }, [users, currentUserId, filters, currentUser]);

  return (
    <div className="pb-20 p-4 max-w-lg mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Find Mates</h1>
        <button 
          onClick={() => setShowFilters(true)}
          className="p-2 bg-slate-100 rounded-full text-slate-600 hover:bg-slate-200"
        >
          <Settings size={20} />
        </button>
      </div>

      <div className="space-y-4">
        {filteredUsers.length === 0 ? (
          <div className="text-center py-20 text-slate-500">
            <Search className="mx-auto mb-4 text-slate-300" size={48} />
            <p>No mates found with these filters.</p>
            <button onClick={() => onFilterChange({ ...filters, gender: 'All', minAge: 18, maxAge: 99, maxDistance: 50 })} className="text-blue-600 mt-2 font-medium">Reset Filters</button>
          </div>
        ) : (
          filteredUsers.map(user => (
            <Link key={user.id} to={`/user/${user.id}`} className="block bg-white rounded-2xl p-4 shadow-sm border border-slate-100 hover:border-blue-100 transition-colors">
              <div className="flex gap-4">
                <div className="w-20 h-20 bg-slate-200 rounded-xl overflow-hidden shrink-0">
                  <img src={`https://picsum.photos/seed/${user.id}/200`} alt={user.name} className="w-full h-full object-cover" />
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-1.5 mb-1">
                    <h3 className="font-bold text-lg truncate">{user.name}, {user.age}</h3>
                    {user.verificationStatus === VerificationStatus.VERIFIED && <CheckCircle size={16} className="text-blue-500 fill-blue-50" />}
                  </div>
                  <p className="text-slate-500 text-sm mb-2 flex items-center gap-1">
                    <MapPin size={14} /> {user.origin} • {calculateDistance(currentUser?.location.lat || 0, currentUser?.location.lng || 0, user.location.lat, user.location.lng).toFixed(1)} km away
                  </p>
                  <div className="flex flex-wrap gap-1">
                    {user.interests.slice(0, 3).map(i => <Badge key={i} color="slate">{i}</Badge>)}
                    {user.goals.map(g => <Badge key={g} color="blue">{g}</Badge>)}
                  </div>
                </div>
              </div>
            </Link>
          ))
        )}
      </div>

      {/* Filter Sidebar/Modal */}
      {showFilters && (
        <div className="fixed inset-0 bg-black/50 z-50 flex justify-end">
          <div className="w-80 bg-white h-full p-6 overflow-y-auto">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-bold">Filters</h2>
              <button onClick={() => setShowFilters(false)}><X /></button>
            </div>

            <div className="space-y-6">
              <div>
                <label className="block text-sm font-semibold mb-2">Gender Preference</label>
                <div className="grid grid-cols-2 gap-2">
                  {['All', Gender.MALE, Gender.FEMALE, Gender.NON_BINARY].map(g => (
                    <button
                      key={g}
                      onClick={() => onFilterChange({ ...filters, gender: g as any })}
                      className={`px-3 py-2 text-sm rounded-lg border transition-colors ${filters.gender === g ? 'bg-blue-600 border-blue-600 text-white' : 'border-slate-200 hover:bg-slate-50'}`}
                    >
                      {g}
                    </button>
                  ))}
                </div>
              </div>

              <div>
                <label className="block text-sm font-semibold mb-2">Age Range: {filters.minAge} - {filters.maxAge}</label>
                <input 
                  type="range" min="18" max="99" value={filters.maxAge} 
                  onChange={(e) => onFilterChange({ ...filters, maxAge: parseInt(e.target.value) })}
                  className="w-full accent-blue-600"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold mb-2">Max Distance: {filters.maxDistance}km</label>
                <input 
                  type="range" min="1" max="100" value={filters.maxDistance} 
                  onChange={(e) => onFilterChange({ ...filters, maxDistance: parseInt(e.target.value) })}
                  className="w-full accent-blue-600"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold mb-2">Interests</label>
                <div className="flex flex-wrap gap-2">
                  {INTEREST_TAGS.map(tag => (
                    <button
                      key={tag}
                      onClick={() => {
                        const newInt = filters.interests.includes(tag) 
                          ? filters.interests.filter(i => i !== tag) 
                          : [...filters.interests, tag];
                        onFilterChange({ ...filters, interests: newInt });
                      }}
                      className={`px-2 py-1 text-xs rounded-full border transition-colors ${filters.interests.includes(tag) ? 'bg-blue-100 border-blue-600 text-blue-700' : 'bg-slate-50 border-slate-200'}`}
                    >
                      {tag}
                    </button>
                  ))}
                </div>
              </div>
            </div>

            <button 
              onClick={() => setShowFilters(false)}
              className="w-full mt-10 bg-blue-600 text-white py-3 rounded-xl font-bold shadow-lg shadow-blue-200"
            >
              Apply Filters
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

const UserDetailView: React.FC<{ users: UserProfile[], currentUserId: string, onMessage: (targetId: string) => void }> = ({ users, currentUserId, onMessage }) => {
  const { id } = useLocation().pathname.split('/').slice(-1)[0] ? { id: useLocation().pathname.split('/').slice(-1)[0] } : { id: '' };
  const navigate = useNavigate();
  const user = users.find(u => u.id === id);
  const [showSafety, setShowSafety] = useState(false);

  if (!user) return <div className="p-4">User not found.</div>;

  return (
    <div className="pb-24 bg-white min-h-screen">
      <div className="relative h-96 bg-slate-200">
        <img src={`https://picsum.photos/seed/${user.id}/600/800`} alt={user.name} className="w-full h-full object-cover" />
        <button onClick={() => navigate(-1)} className="absolute top-4 left-4 p-2 bg-white/20 backdrop-blur-md rounded-full text-white">
          <ChevronLeft />
        </button>
        <button className="absolute top-4 right-4 p-2 bg-white/20 backdrop-blur-md rounded-full text-white">
          <Flag size={20} />
        </button>
      </div>

      <div className="px-6 py-8 -mt-10 bg-white rounded-t-[40px] relative">
        <div className="flex justify-between items-start mb-4">
          <div>
            <div className="flex items-center gap-2 mb-1">
              <h1 className="text-3xl font-bold">{user.name}, {user.age}</h1>
              {user.verificationStatus === VerificationStatus.VERIFIED && <CheckCircle className="text-blue-500" />}
            </div>
            <p className="text-slate-500 font-medium">{user.origin} • {user.gender}</p>
          </div>
          <div className="flex flex-col items-center p-2 bg-slate-50 rounded-2xl border border-slate-100">
            <div className="flex items-center gap-1 text-yellow-500">
              <Star size={18} fill="currentColor" />
              <span className="font-bold text-slate-800">{user.rating}</span>
            </div>
            <span className="text-[10px] text-slate-400 font-medium uppercase tracking-wider">{user.ratingCount} reviews</span>
          </div>
        </div>

        <div className="mb-6">
          <h2 className="text-sm font-bold uppercase tracking-widest text-slate-400 mb-2">About</h2>
          <p className="text-slate-700 leading-relaxed">{user.bio}</p>
        </div>

        <div className="mb-6">
          <h2 className="text-sm font-bold uppercase tracking-widest text-slate-400 mb-2">Interests</h2>
          <div className="flex flex-wrap gap-2">
            {user.interests.map(i => <Badge key={i} color="blue">{i}</Badge>)}
          </div>
        </div>

        <div className="mb-6">
          <h2 className="text-sm font-bold uppercase tracking-widest text-slate-400 mb-2">Languages</h2>
          <div className="flex flex-wrap gap-2">
            {user.languages.map(l => <span key={l} className="px-3 py-1 bg-slate-100 rounded-lg text-sm text-slate-600 font-medium">{l}</span>)}
          </div>
        </div>

        <div className="fixed bottom-24 left-6 right-6 flex gap-3">
          <button 
            onClick={() => {
              setShowSafety(true);
              setTimeout(() => {
                setShowSafety(false);
                onMessage(user.id);
                navigate('/chats');
              }, 3000);
            }}
            className="flex-1 bg-blue-600 text-white py-4 rounded-2xl font-bold flex items-center justify-center gap-2 shadow-xl shadow-blue-200 active:scale-95 transition-transform"
          >
            <MessageCircle size={20} />
            Send Message
          </button>
        </div>
      </div>

      {showSafety && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-6 bg-black/60 backdrop-blur-sm">
          <div className="bg-white rounded-3xl p-8 max-w-sm text-center shadow-2xl animate-in fade-in zoom-in">
            <div className="w-16 h-16 bg-blue-100 text-blue-600 rounded-2xl flex items-center justify-center mx-auto mb-6">
              <ShieldCheck size={40} />
            </div>
            <h2 className="text-2xl font-bold mb-4">Safety First!</h2>
            <ul className="text-slate-600 text-sm space-y-3 text-left mb-6">
              <li className="flex gap-2">✅ Meet in public places only.</li>
              <li className="flex gap-2">✅ Inform a friend about your plans.</li>
              <li className="flex gap-2">✅ Trust your intuition.</li>
            </ul>
            <div className="h-1 bg-slate-100 rounded-full overflow-hidden">
              <div className="h-full bg-blue-600 w-1/2 animate-[progress_3s_linear]"></div>
            </div>
            <p className="text-[10px] text-slate-400 mt-2">Redirecting to chat safely...</p>
          </div>
        </div>
      )}
    </div>
  );
};

const ActivitiesView: React.FC<{ activities: Activity[], currentUserId: string, onJoin: (aid: string) => void }> = ({ activities, currentUserId, onJoin }) => {
  const [filter, setFilter] = useState('All');
  const types = ['All', ...new Set(activities.map(a => a.type))];

  return (
    <div className="pb-20 p-4 max-w-lg mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Activities</h1>
        <button className="bg-blue-600 text-white p-2 rounded-full shadow-lg shadow-blue-200">
          <PlusCircle size={24} />
        </button>
      </div>

      <div className="flex gap-2 overflow-x-auto pb-4 no-scrollbar">
        {types.map(t => (
          <button
            key={t}
            onClick={() => setFilter(t)}
            className={`px-4 py-2 rounded-full whitespace-nowrap text-sm font-medium transition-colors ${filter === t ? 'bg-blue-600 text-white' : 'bg-white text-slate-500 border border-slate-200'}`}
          >
            {t}
          </button>
        ))}
      </div>

      <div className="space-y-4">
        {activities.filter(a => filter === 'All' || a.type === filter).map(activity => {
          const isParticipant = activity.participants.includes(currentUserId);
          return (
            <div key={activity.id} className="bg-white rounded-2xl border border-slate-100 overflow-hidden shadow-sm">
              <div className="p-4">
                <div className="flex justify-between items-start mb-2">
                  <Badge color="blue">{activity.type}</Badge>
                  <span className="text-xs text-slate-400 font-medium">{new Date(activity.dateTime).toLocaleDateString()}</span>
                </div>
                <h3 className="font-bold text-lg mb-1">{activity.title}</h3>
                <p className="text-slate-500 text-sm mb-4 flex items-center gap-1">
                  <MapPin size={14} /> {activity.locationName}
                </p>
                <div className="flex items-center gap-2 mb-4">
                  <div className="flex -space-x-2">
                    {activity.participants.slice(0, 3).map(pid => (
                      <div key={pid} className="w-8 h-8 rounded-full border-2 border-white bg-slate-200 overflow-hidden">
                        <img src={`https://picsum.photos/seed/${pid}/50`} alt="Avatar" />
                      </div>
                    ))}
                    {activity.participants.length > 3 && (
                      <div className="w-8 h-8 rounded-full border-2 border-white bg-slate-100 flex items-center justify-center text-[10px] font-bold text-slate-400">
                        +{activity.participants.length - 3}
                      </div>
                    )}
                  </div>
                  <span className="text-xs text-slate-400">{activity.participants.length} joining</span>
                </div>
                <button 
                  onClick={() => onJoin(activity.id)}
                  className={`w-full py-2.5 rounded-xl font-bold transition-all ${isParticipant ? 'bg-slate-100 text-slate-500 cursor-default' : 'bg-blue-600 text-white active:scale-95'}`}
                >
                  {isParticipant ? 'Joined' : 'Join Activity'}
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

const ChatListView: React.FC<{ conversations: Conversation[], users: UserProfile[] }> = ({ conversations, users }) => {
  return (
    <div className="pb-20 p-4 max-w-lg mx-auto">
      <h1 className="text-2xl font-bold mb-6">Messages</h1>
      <div className="space-y-2">
        {conversations.length === 0 ? (
          <div className="text-center py-20 text-slate-400">No conversations yet.</div>
        ) : (
          conversations.map(conv => {
            const otherUser = users.find(u => conv.participants.includes(u.id) && u.id !== 'u1'); // Assuming u1 is me
            if (!otherUser) return null;
            return (
              <Link key={conv.id} to={`/chat/${conv.id}`} className="flex items-center gap-4 p-4 bg-white rounded-2xl border border-slate-100 hover:border-blue-100 transition-colors">
                <div className="w-14 h-14 bg-slate-200 rounded-full overflow-hidden shrink-0">
                  <img src={`https://picsum.photos/seed/${otherUser.id}/100`} alt={otherUser.name} />
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex justify-between items-center mb-1">
                    <h3 className="font-bold truncate">{otherUser.name}</h3>
                    <span className="text-[10px] text-slate-400 font-medium">2m ago</span>
                  </div>
                  <p className="text-sm text-slate-500 truncate">{conv.lastMessage || 'Say hi to your new mate!'}</p>
                </div>
                <div className="w-2 h-2 bg-blue-600 rounded-full"></div>
              </Link>
            );
          })
        )}
      </div>
    </div>
  );
};

const ChatDetailView: React.FC<{ users: UserProfile[], currentUserId: string }> = ({ users, currentUserId }) => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState<any[]>([]);
  const [input, setInput] = useState('');

  const sendMessage = () => {
    if (!input.trim()) return;
    setMessages([...messages, { id: Date.now(), senderId: currentUserId, text: input, timestamp: new Date().toISOString() }]);
    setInput('');
  };

  return (
    <div className="flex flex-col h-screen bg-white">
      <div className="flex items-center gap-4 p-4 border-b bg-white sticky top-0 z-10">
        <button onClick={() => navigate(-1)}><ChevronLeft /></button>
        <div className="w-10 h-10 bg-slate-200 rounded-full overflow-hidden">
          <img src={`https://picsum.photos/seed/other/100`} alt="Other" />
        </div>
        <div>
          <h3 className="font-bold leading-none">Buddy</h3>
          <span className="text-[10px] text-green-500 font-bold uppercase tracking-widest">Online</span>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map(m => (
          <div key={m.id} className={`flex ${m.senderId === currentUserId ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-[80%] px-4 py-2 rounded-2xl ${m.senderId === currentUserId ? 'bg-blue-600 text-white rounded-br-none' : 'bg-slate-100 text-slate-800 rounded-bl-none'}`}>
              <p className="text-sm">{m.text}</p>
              <span className="text-[8px] opacity-60 mt-1 block text-right">{new Date(m.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
            </div>
          </div>
        ))}
      </div>

      <div className="p-4 border-t bg-white mb-20 md:mb-0">
        <div className="flex gap-2">
          <input 
            type="text" 
            value={input} 
            onChange={e => setInput(e.target.value)}
            onKeyPress={e => e.key === 'Enter' && sendMessage()}
            placeholder="Write a message..." 
            className="flex-1 bg-slate-50 rounded-xl px-4 py-3 outline-none focus:ring-2 focus:ring-blue-100 border border-transparent focus:border-blue-200" 
          />
          <button 
            onClick={sendMessage}
            className="bg-blue-600 text-white p-3 rounded-xl shadow-lg shadow-blue-100"
          >
            <Send size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

const ProfileView: React.FC<{ user: UserProfile }> = ({ user }) => {
  return (
    <div className="pb-24 p-6 max-w-lg mx-auto">
      <div className="flex flex-col items-center mb-8">
        <div className="w-32 h-32 rounded-[40px] bg-slate-200 p-1 border-2 border-blue-100 mb-4 shadow-xl shadow-blue-50">
          <img src={`https://picsum.photos/seed/${user.id}/300`} className="w-full h-full object-cover rounded-[38px]" alt="Me" />
        </div>
        <div className="flex items-center gap-2 mb-1">
          <h1 className="text-2xl font-bold">{user.name}, {user.age}</h1>
          <CheckCircle className="text-blue-500" size={20} />
        </div>
        <p className="text-slate-500">{user.origin}</p>
      </div>

      <div className="space-y-4">
        <div className="bg-white p-5 rounded-3xl border border-slate-100 shadow-sm">
          <div className="flex justify-between items-center mb-4">
            <h2 className="font-bold">Trust Score</h2>
            <div className="flex items-center gap-1 text-yellow-500">
              <Star size={16} fill="currentColor" />
