  const filteredUsers = useMemo(() => {
    return users.filter(user => {
      if (user.id === currentUserId) return false;
      if (filters.gender !== 'All' && user.gender !== filters.gender) return false;
      if (user.age < filters.minAge || user.age > filters.maxAge) return false;
      const dist = calculateDistance(currentUser.location.lat, currentUser.location.lng, user.location.lat, user.location.lng);
      if (dist > filters.maxDistance) return false;
      if (filters.interests.length > 0 && !filters.interests.some(i => user.interests.includes(i))) return false;
      return true;
    });
  }, [users, filters, currentUser]);

  const filteredActivities = useMemo(() => {
    return activities.filter(a => {
        const dist = calculateDistance(currentUser.location.lat, currentUser.location.lng, a.location.lat, a.location.lng);
