// --- Draggable Map Component ---

const InteractiveMap: React.FC<{ 
  users: UserProfile[], 
  activities: Activity[], 
  onSelectUser: (u: UserProfile) => void,
  onSelectActivity: (a: Activity) => void,
  currentUser: UserProfile,
  showMates?: boolean,
  showActivities?: boolean
}> = ({ users, activities, onSelectUser, onSelectActivity, currentUser, showMates = true, showActivities = true }) => {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const mapRef = useRef<HTMLDivElement>(null);
  const lastPos = useRef({ x: 0, y: 0 });

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const dx = e.clientX - lastPos.current.x;
    const dy = e.clientY - lastPos.current.y;
    setOffset(prev => ({ x: prev.x + dx, y: prev.y + dy }));
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseUp = () => setIsDragging(false);

  // Simulated coordinate mapping
  const getPos = (lat: number, lng: number) => {
    const latScale = 4000;
    const lngScale = 4000;
    const top = 50 - (lat - 48.8566) * latScale;
    const left = 50 + (lng - 2.3522) * lngScale;
    return { 
      top: `calc(${top}% + ${offset.y}px)`, 
      left: `calc(${left}% + ${offset.x}px)` 
    };
  };

  return (
    <div 
      ref={mapRef}
      className={`relative w-full h-full bg-slate-100 overflow-hidden cursor-${isDragging ? 'grabbing' : 'grab'}`}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* Background patterns */}
      <div className="absolute inset-0 pointer-events-none opacity-[0.03]" 
           style={{ 
             backgroundImage: 'radial-gradient(#000 1px, transparent 1px)', 
             backgroundSize: '40px 40px',
             transform: `translate(${offset.x % 40}px, ${offset.y % 40}px)` 
           }} />
      <div className="absolute inset-0 pointer-events-none opacity-[0.02]" 
           style={{ 
             backgroundImage: 'linear-gradient(#000 1px, transparent 1px), linear-gradient(90deg, #000 1px, transparent 1px)', 
             backgroundSize: '200px 200px',
             transform: `translate(${offset.x % 200}px, ${offset.y % 200}px)`
           }} />

      {/* Origin/Current User Marker */}
      <div className="absolute transition-transform duration-100 z-10" style={getPos(currentUser.location.lat, currentUser.location.lng)}>
        <div className="relative -translate-x-1/2 -translate-y-1/2">
