          <h1 className="text-4xl font-black text-slate-900 tracking-tighter uppercase">Chats</h1>
          <p className="text-[10px] font-black text-blue-600 uppercase tracking-[0.3em] mt-2">Active connections</p>
        </div>
        <div className="space-y-4">
          {conversations.length === 0 ? (
            <div className="text-center py-24 bg-white rounded-[40px] border border-slate-100">
              <MessageCircle size={48} className="mx-auto mb-6 text-slate-200" />
              <p className="text-slate-400 font-black uppercase text-xs tracking-widest">No active messages</p>
            </div>
          ) : (
            conversations.map(conv => {
              const otherUser = users.find(u => conv.participants.includes(u.id) && u.id !== 'u1');
              if (!otherUser) return null;
              return (
                <Link key={conv.id} to={`/chat/${conv.id}`} className="flex items-center gap-5 p-6 bg-white rounded-[32px] border border-slate-100 hover:border-blue-200 shadow-sm transition-all group">
                  <div className="relative">
                      <img src={`https://picsum.photos/seed/${otherUser.id}/100`} className="w-16 h-16 rounded-2xl object-cover shadow-md" />
                      <div className="absolute -bottom-1 -right-1 w-5 h-5 bg-green-500 border-4 border-white rounded-full"></div>
                  </div>
                  <div className="flex-1 min-w-0">
                    <h3 className="font-black text-slate-900 truncate text-lg">{otherUser.name}</h3>
                    <p className="text-sm text-slate-500 truncate font-bold">{conv.lastMessage}</p>
                  </div>
                </Link>
              );
            })
          )}
        </div>
      </div>
    );
  };

  const ChatDetailView: React.FC<{ users: UserProfile[], currentUserId: string }> = ({ users, currentUserId }) => {
    const navigate = useNavigate();
    const [messages, setMessages] = useState<any[]>([]);
    const [input, setInput] = useState('');

    const sendMessage = () => {
      if (!input.trim()) return;
      setMessages([...messages, { id: Date.now(), senderId: currentUserId, text: input, timestamp: new Date().toISOString() }]);
      setInput('');
    };

    return (
      <div className="flex flex-col h-screen bg-white">
        <div className="flex items-center gap-5 px-8 py-6 border-b bg-white/80 backdrop-blur-xl sticky top-0 z-10">
          <button onClick={() => navigate(-1)} className="p-3 bg-slate-50 rounded-2xl text-slate-400"><ChevronLeft /></button>
          <div className="w-12 h-12 rounded-2xl overflow-hidden shadow-md">
            <img src={`https://picsum.photos/seed/other/100`} alt="Other" className="w-full h-full object-cover" />
          </div>
          <div className="flex-1">
            <h3 className="font-black text-slate-900 leading-none mb-1">Buddy</h3>
            <span className="text-[10px] text-green-500 font-black uppercase tracking-widest">Active now</span>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto px-8 py-10 space-y-6">
          {messages.map(m => (
            <div key={m.id} className={`flex ${m.senderId === currentUserId ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-[80%] px-6 py-4 rounded-[28px] shadow-sm ${m.senderId === currentUserId ? 'bg-blue-600 text-white rounded-br-none' : 'bg-slate-100 text-slate-800 rounded-bl-none'}`}>
                <p className="text-sm font-bold leading-relaxed">{m.text}</p>
                <span className={`text-[9px] opacity-60 mt-2 block font-black uppercase ${m.senderId === currentUserId ? 'text-right' : 'text-left'}`}>{new Date(m.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
              </div>
            </div>
          ))}
        </div>

        <div className="px-8 py-6 border-t bg-white mb-20 md:mb-0">
          <div className="flex gap-4">
            <input 
              type="text" value={input} 
              onChange={e => setInput(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && sendMessage()}
              placeholder="Start typing..." 
              className="flex-1 bg-slate-50 rounded-[28px] px-8 py-5 outline-none focus:ring-4 focus:ring-blue-100 border border-transparent font-bold transition-all" 
            />
            <button onClick={sendMessage} className="bg-blue-600 text-white p-5 rounded-[28px] shadow-2xl shadow-blue-200"><Send size={24}/></button>
          </div>
        </div>
      </div>
    );
  };

