// --- Stylized Interactive Map Component ---

const InteractiveMap: React.FC<{ 
  activities: Activity[], 
  onSelectActivity: (a: Activity) => void,
  currentUser: UserProfile,
}> = ({ activities, onSelectActivity, currentUser }) => {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const mapRef = useRef<HTMLDivElement>(null);
  const lastPos = useRef({ x: 0, y: 0 });

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const dx = e.clientX - lastPos.current.x;
    const dy = e.clientY - lastPos.current.y;
    setOffset(prev => ({ x: prev.x + dx, y: prev.y + dy }));
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseUp = () => setIsDragging(false);

  const getPos = (lat: number, lng: number) => {
    const latScale = 5000;
    const lngScale = 5000;
    const top = 50 - (lat - 48.8566) * latScale;
    const left = 50 + (lng - 2.3522) * lngScale;
    return { 
      top: `calc(${top}% + ${offset.y}px)`, 
      left: `calc(${left}% + ${offset.x}px)` 
    };
  };

  return (
    <div 
      ref={mapRef}
      className={`relative w-full h-full bg-[#f8fafc] overflow-hidden cursor-${isDragging ? 'grabbing' : 'grab'}`}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* Visual background layers */}
      <div className="absolute inset-0 pointer-events-none opacity-[0.05]" 
           style={{ 
             backgroundImage: 'radial-gradient(#2563eb 1px, transparent 1px)', 
             backgroundSize: '30px 30px',
             transform: `translate(${offset.x % 30}px, ${offset.y % 30}px)` 
           }} />

      {/* City blocks simulation */}
      <div className="absolute inset-0 pointer-events-none opacity-[0.03]" 
           style={{ 
             backgroundImage: 'linear-gradient(#000 2px, transparent 2px), linear-gradient(90deg, #000 2px, transparent 2px)', 
             backgroundSize: '150px 150px',
             transform: `translate(${offset.x % 150}px, ${offset.y % 150}px)`
           }} />

      {/* Origin: Current User Location */}
      <div className="absolute z-10 transition-transform" style={getPos(currentUser.location.lat, currentUser.location.lng)}>
        <div className="relative -translate-x-1/2 -translate-y-1/2">
           <div className="w-8 h-8 bg-blue-600 rounded-full border-2 border-white shadow-lg flex items-center justify-center animate-pulse">
              <div className="w-2 h-2 bg-white rounded-full"></div>
           </div>
           <div className="absolute -bottom-6 left-1/2 -translate-x-1/2 text-[8px] font-black uppercase text-blue-600 tracking-widest whitespace-nowrap">Your Spot</div>
        </div>
      </div>

      {/* Activity Markers Only */}
      {activities.map(a => (
        <button 
          key={a.id} 
          onClick={(e) => { e.stopPropagation(); onSelectActivity(a); }}
          className="absolute transition-all hover:scale-125 z-20 group"
          style={getPos(a.location.lat, a.location.lng)}
        >
          <div className="relative -translate-x-1/2 -translate-y-1/2">
            <div className={`w-12 h-12 rounded-3xl flex items-center justify-center border-4 border-white shadow-xl transition-all ${a.type === 'Culture' ? 'bg-indigo-500' : 'bg-orange-500'}`}>
              <Calendar size={20} className="text-white" />
              <div className="absolute -top-1 -right-1 bg-white text-[8px] font-black px-1.5 py-0.5 rounded-full text-slate-800 shadow-sm border border-slate-100">
                {a.interestedUserIds.length}
              </div>
            </div>
            <div className="absolute -bottom-10 left-1/2 -translate-x-1/2 opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap bg-white/90 backdrop-blur px-3 py-1.5 rounded-xl shadow-lg border border-slate-100">
                <p className="text-[10px] font-black uppercase tracking-widest text-slate-900">{a.title}</p>
            </div>
          </div>
        </button>
      ))}

      {/* Controls Overlay */}
      <div className="absolute bottom-6 right-6 flex flex-col gap-2">
          <div className="bg-white/80 backdrop-blur p-4 rounded-3xl border border-white shadow-xl">
             <div className="flex items-center gap-2 mb-2">
                <div className="w-2 h-2 bg-indigo-500 rounded-full"></div>
                <span className="text-[9px] font-black uppercase tracking-widest text-slate-400">Culture</span>
             </div>
             <div className="flex items-center gap-2">
                <div className="w-2 h-2 bg-orange-500 rounded-full"></div>
                <span className="text-[9px] font-black uppercase tracking-widest text-slate-400">Activity</span>
             </div>
          </div>
      </div>
    </div>
  );
};

