const InteractiveMap: React.FC<{ 
  activities: Activity[], 
  onSelectActivity: (a: Activity) => void,
  currentUser: UserProfile,
}> = ({ activities, onSelectActivity, currentUser }) => {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const mapRef = useRef<HTMLDivElement>(null);
  const lastPos = useRef({ x: 0, y: 0 });

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const dx = e.clientX - lastPos.current.x;
    const dy = e.clientY - lastPos.current.y;
    setOffset(prev => ({ x: prev.x + dx, y: prev.y + dy }));
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseUp = () => setIsDragging(false);

  const getPos = (lat: number, lng: number) => {
    const latScale = 6000;
    const lngScale = 6000;
    const top = 50 - (lat - 48.8566) * latScale;
    const left = 50 + (lng - 2.3522) * lngScale;
    return { 
      top: `calc(${top}% + ${offset.y}px)`, 
      left: `calc(${left}% + ${offset.x}px)` 
    };
  };

  return (
    <div 
      ref={mapRef}
      className={`relative w-full h-full bg-[#f1f5f9] overflow-hidden cursor-${isDragging ? 'grabbing' : 'grab'}`}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* GRAPHIC BACKGROUND: Blueprint / Sketch Style */}
      <div className="absolute inset-0 pointer-events-none opacity-[0.08]" 
           style={{ 
             backgroundImage: 'radial-gradient(#1e293b 1px, transparent 1px)', 
             backgroundSize: '40px 40px',
             transform: `translate(${offset.x % 40}px, ${offset.y % 40}px)` 
           }} />
      <div className="absolute inset-0 pointer-events-none opacity-[0.04]" 
           style={{ 
             backgroundImage: 'linear-gradient(#1e293b 2px, transparent 2px), linear-gradient(90deg, #1e293b 2px, transparent 2px)', 
             backgroundSize: '200px 200px',
             transform: `translate(${offset.x % 200}px, ${offset.y % 200}px)`
           }} />
      {/* Fictive city roads simulation */}
      <div className="absolute inset-0 pointer-events-none opacity-[0.02] border-[10px] border-slate-900/10 rounded-full scale-[2]"
           style={{ transform: `translate(${offset.x/2}px, ${offset.y/2}px)` }} />

      {/* Origin: Current User Location */}
      <div className="absolute z-10" style={getPos(currentUser.location.lat, currentUser.location.lng)}>
        <div className="relative -translate-x-1/2 -translate-y-1/2">
           <div className="w-10 h-10 bg-blue-600 rounded-full border-4 border-white shadow-2xl flex items-center justify-center animate-pulse">
              <div className="w-2.5 h-2.5 bg-white rounded-full"></div>
           </div>
        </div>
      </div>

      {/* Activity Markers */}
      {activities.map(a => (
        <button 
          key={a.id} 
          onClick={(e) => { e.stopPropagation(); onSelectActivity(a); }}
          className="absolute transition-all hover:scale-125 z-20 group"
          style={getPos(a.location.lat, a.location.lng)}
        >
          <div className="relative -translate-x-1/2 -translate-y-1/2">
            <div className={`w-14 h-14 rounded-[24px] flex items-center justify-center border-4 border-white shadow-2xl transition-all ${a.type === 'Culture' ? 'bg-indigo-600' : 'bg-orange-500'}`}>
              <Calendar size={24} className="text-white" />
              <div className="absolute -top-2 -right-2 bg-slate-900 text-white text-[9px] font-black px-2 py-0.5 rounded-full shadow-lg border-2 border-white">
                {a.interestedUserIds.length}
              </div>
            </div>
          </div>
        </button>
      ))}

      <div className="absolute bottom-10 left-10 pointer-events-none opacity-30 select-none">
        <h3 className="text-3xl font-black text-slate-400 uppercase tracking-tighter italic">Paris Exploration Map</h3>
        <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mt-1">50+ LIVE ACTIVITIES</p>
      </div>
    </div>
  );
};

