                                    onMessage(firstMate, `Hey! I saw you're interested in ${selectedActivity.title}. Want to go together?`);
                                    navigate('/chats');
                                }
                            }}
                            className="p-5 bg-blue-600 text-white rounded-[28px] shadow-2xl shadow-blue-200 active:scale-95 transition-all flex items-center justify-center"
                            title="Go together!"
                        >
                            <HandMetal size={28} />
                        </button>
                    </div>
                </div>
            </div>
        )}
      </div>
    </div>
  );
};

