}> = ({ activities, onSelectActivity, currentUser }) => {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const lastPos = useRef({ x: 0, y: 0 });

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const dx = e.clientX - lastPos.current.x;
    const dy = e.clientY - lastPos.current.y;
    setOffset(prev => ({ x: prev.x + dx, y: prev.y + dy }));
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseUp = () => setIsDragging(false);

  const getPos = (lat: number, lng: number) => {
    const latScale = 7500;
    const lngScale = 7500;
    const top = 50 - (lat - 48.8566) * latScale;
    const left = 50 + (lng - 2.3522) * lngScale;
    return { 
      top: `calc(${top}% + ${offset.y}px)`, 
      left: `calc(${left}% + ${offset.x}px)` 
    };
  };

  return (
    <div 
      className={`relative w-full h-full bg-[#f8fafc] overflow-hidden cursor-${isDragging ? 'grabbing' : 'grab'}`}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* FICTIVE GRAPHIC BACKGROUND: Cyber Blueprint / Map Art */}
      <div className="absolute inset-0 pointer-events-none transition-transform duration-75" style={{ transform: `translate(${offset.x}px, ${offset.y}px)` }}>
          {/* Subtle Grid */}
          <div className="absolute inset-[-2000px] opacity-[0.05]" 
               style={{ backgroundImage: 'linear-gradient(#6366f1 1px, transparent 1px), linear-gradient(90deg, #6366f1 1px, transparent 1px)', backgroundSize: '100px 100px' }} />

          {/* Fictive Landmarks / Circles */}
          <div className="absolute top-[50%] left-[50%] -translate-x-1/2 -translate-y-1/2 w-[800px] h-[800px] rounded-full border-[1px] border-indigo-600/10 opacity-20 pointer-events-none" />
          <div className="absolute top-[50%] left-[50%] -translate-x-1/2 -translate-y-1/2 w-[1600px] h-[1600px] rounded-full border-[1px] border-indigo-600/5 opacity-10 pointer-events-none" />

          {/* Fictive District Text */}
          <div className="absolute top-[48%] left-[52%] font-black text-slate-200 text-6xl opacity-30 select-none tracking-tighter uppercase italic">Paris Center</div>
          <div className="absolute top-[40%] left-[40%] font-black text-slate-200 text-4xl opacity-20 select-none tracking-tighter uppercase">Nord District</div>
          <div className="absolute top-[60%] left-[60%] font-black text-slate-200 text-4xl opacity-20 select-none tracking-tighter uppercase">South Sector</div>
      </div>

      {/* User Origin Indicator */}
      <div className="absolute z-10" style={getPos(currentUser.location.lat, currentUser.location.lng)}>
        <div className="relative -translate-x-1/2 -translate-y-1/2">
           <div className="w-12 h-12 bg-indigo-600 rounded-full border-4 border-white shadow-2xl flex items-center justify-center animate-pulse">
              <div className="w-3 h-3 bg-white rounded-full"></div>
           </div>
        </div>
      </div>

      {/* Activity Markers Only */}
      {activities.map(a => (
        <button 
          key={a.id} 
          onClick={(e) => { e.stopPropagation(); onSelectActivity(a); }}
          className="absolute transition-all hover:scale-125 z-20 group"
          style={getPos(a.location.lat, a.location.lng)}
        >
          <div className="relative -translate-x-1/2 -translate-y-1/2">
            <div className={`w-14 h-14 rounded-[26px] flex items-center justify-center border-4 border-white shadow-[0_20px_50px_rgba(0,0,0,0.1)] transition-all ${a.type === 'Culture' ? 'bg-indigo-600' : 'bg-orange-500'}`}>
              <MapIcon size={24} className="text-white" />
              <div className="absolute -top-2 -right-2 bg-slate-900 text-white text-[9px] font-black px-2 py-0.5 rounded-full shadow-lg border-2 border-white">
                {a.interestedUserIds.length}
              </div>
            </div>
          </div>
        </button>
      ))}

      <div className="absolute top-10 left-10 pointer-events-none opacity-40 select-none">
        <h3 className="text-2xl font-black text-indigo-900/50 uppercase tracking-tighter italic leading-none">Discovery Blueprint</h3>
        <p className="text-[9px] font-black text-indigo-900/40 uppercase tracking-[0.4em] mt-2">SIMULATED GEOSPACE v1.0</p>
      </div>
    </div>
  );
};

