// --- Main App ---

export default function App() {
  const [currentUserId] = useState('u1');
  const [users] = useState<UserProfile[]>(MOCK_USERS);
  const [activities, setActivities] = useState<Activity[]>(MOCK_ACTIVITIES);
  const [conversations, setConversations] = useState<Conversation[]>([]);

  const currentUser = users.find(u => u.id === currentUserId)!;

  const handleJoinActivity = (aid: string) => {
    setActivities(prev => prev.map(a => 
      a.id === aid && !a.participants.includes(currentUserId) 
      ? { ...a, participants: [...a.participants, currentUserId] } 
      : a
    ));
  };

  const startConversation = (targetId: string, text?: string) => {
    const existingIdx = conversations.findIndex(c => c.participants.includes(targetId));
    if (existingIdx === -1) {
      const newConv: Conversation = {
        id: `c${Date.now()}`,
        participants: [currentUserId, targetId],
        lastMessage: text || 'Hey! Ready to explore together?',
        lastMessageTime: new Date().toISOString()
      };
      setConversations([newConv, ...conversations]);
    } else {
        const updated = [...conversations];
        if (text) updated[existingIdx].lastMessage = text;
        setConversations(updated);
    }
  };

  return (
    <HashRouter>
      <div className="min-h-screen bg-white">
        <Routes>
          <Route path="/" element={<Navigate to="/explore" />} />
          <Route path="/explore" element={
            <ExploreView 
              activities={activities}
              users={users}
              currentUser={currentUser} 
              onMessage={startConversation}
              onJoin={handleJoinActivity}
            />
          } />
          <Route path="/mates" element={
            <MatesListView users={users} currentUser={currentUser} />
          } />
          <Route path="/user/:id" element={
            <UserDetailView users={users} currentUserId={currentUserId} onMessage={startConversation} />
          } />
          <Route path="/chats" element={<ChatListView conversations={conversations} users={users} />} />
          <Route path="/chat/:id" element={<ChatDetailView users={users} currentUserId={currentUserId} />} />
          <Route path="/profile" element={<ProfileView user={currentUser} />} />
        </Routes>
        <Navbar />
      </div>

      <style>{`
        @keyframes progress { from { width: 0%; } to { width: 100%; } }
        ::-webkit-scrollbar { width: 0px; background: transparent; }
      `}</style>
    </HashRouter>
  );
}

