const ChatDetailView: React.FC<{ users: UserProfile[], currentUserId: string }> = ({ users, currentUserId }) => {
    const navigate = useNavigate();
    const [messages, setMessages] = useState<any[]>([]);
    const [input, setInput] = useState('');

    const sendMessage = () => {
      if (!input.trim()) return;
      setMessages([...messages, { id: Date.now(), senderId: currentUserId, text: input, timestamp: new Date().toISOString() }]);
      setInput('');
    };

    return (
      <div className="flex flex-col h-screen bg-white">
        <div className="flex items-center gap-8 px-12 py-10 border-b bg-white/90 backdrop-blur-2xl sticky top-0 z-10">
          <button onClick={() => navigate(-1)} className="p-5 bg-slate-50 rounded-[24px] text-slate-400 active:scale-90 transition-all"><ChevronLeft size={24}/></button>
          <div className="flex flex-col">
            <h3 className="font-black text-slate-900 text-xl uppercase tracking-widest leading-none mb-1">Mate Connection</h3>
            <span className="text-[11px] text-indigo-500 font-black uppercase tracking-widest">Global Traveler</span>
          </div>
        </div>
        <div className="flex-1 overflow-y-auto px-12 py-16 space-y-10">
          {messages.map(m => (
            <div key={m.id} className={`flex ${m.senderId === currentUserId ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-[85%] px-9 py-6 rounded-[44px] shadow-sm ${m.senderId === currentUserId ? 'bg-indigo-600 text-white rounded-br-none' : 'bg-slate-50 text-slate-800 rounded-bl-none'}`}>
                <p className="text-base font-bold leading-relaxed">{m.text}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="px-12 py-10 border-t bg-white mb-28 md:mb-0">
          <div className="flex gap-6">
            <input 
              type="text" value={input} 
              onChange={e => setInput(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && sendMessage()}
              placeholder="Your message..." 
              className="flex-1 bg-slate-50 rounded-[44px] px-10 py-7 outline-none font-bold placeholder:text-slate-300 transition-all" 
            />
            <button onClick={sendMessage} className="bg-indigo-600 text-white p-7 rounded-[44px] shadow-2xl active:scale-90 transition-all"><Send size={32}/></button>
          </div>
        </div>
      </div>
    );
