const InteractiveMap: React.FC<{ 
  activities: Activity[], 
  onSelectActivity: (a: Activity) => void,
  currentUser: UserProfile,
}> = ({ activities, onSelectActivity, currentUser }) => {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const lastPos = useRef({ x: 0, y: 0 });

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const dx = e.clientX - lastPos.current.x;
    const dy = e.clientY - lastPos.current.y;
    setOffset(prev => ({ x: prev.x + dx, y: prev.y + dy }));
    lastPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseUp = () => setIsDragging(false);

  const getPos = (lat: number, lng: number) => {
    const latScale = 8000;
    const lngScale = 8000;
    const top = 50 - (lat - 48.8566) * latScale;
    const left = 50 + (lng - 2.3522) * lngScale;
    return { 
      top: `calc(${top}% + ${offset.y}px)`, 
      left: `calc(${left}% + ${offset.x}px)` 
    };
  };

  return (
    <div 
      className={`relative w-full h-full bg-[#E5F0F9] overflow-hidden cursor-${isDragging ? 'grabbing' : 'grab'}`}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* GRAPHIC STYLIZED MAP BACKGROUND (Image mimic) */}
      <div className="absolute inset-[-2000px] pointer-events-none transition-transform duration-75" style={{ transform: `translate(${offset.x}px, ${offset.y}px)` }}>
          <svg className="absolute inset-0 w-full h-full opacity-30" xmlns="http://www.w3.org/2000/svg">
              {/* Simulated Rivers */}
              <path d="M0,500 Q200,450 400,600 T800,550 T1200,700 T1600,600 T2000,800 T2400,750 T2800,900 T3200,850 T3600,1000 T4000,950" fill="none" stroke="#A7C7E7" strokeWidth="100" />
              <path d="M500,0 Q600,400 550,800 T700,1200 T600,1600 T800,2000 T750,2400 T900,2800 T850,3200 T1000,3600 T950,4000" fill="none" stroke="#A7C7E7" strokeWidth="60" />
              {/* Simulated Roads */}
              <g stroke="#FFFFFF" strokeWidth="15">
                  {Array.from({ length: 20 }).map((_, i) => (
                      <React.Fragment key={i}>
                        <line x1="0" y1={i * 200} x2="4000" y2={i * 200 + 100} />
                        <line x1={i * 200} y1="0" x2={i * 200 + 100} y2="4000" />
                      </React.Fragment>
                  ))}
              </g>
          </svg>
      </div>

      {/* Origin: Current User Location */}
      <div className="absolute z-10" style={getPos(currentUser.location.lat, currentUser.location.lng)}>
        <div className="relative -translate-x-1/2 -translate-y-1/2">
           <div className="w-12 h-12 bg-white rounded-full border-4 border-indigo-600 shadow-2xl flex items-center justify-center animate-pulse">
              <div className="w-3 h-3 bg-indigo-600 rounded-full"></div>
           </div>
           <div className="absolute -bottom-6 left-1/2 -translate-x-1/2 bg-indigo-600 text-white text-[8px] px-2 py-0.5 rounded-full font-black tracking-widest whitespace-nowrap shadow-lg">MA POSITION</div>
        </div>
      </div>

      {/* Activity Markers */}
      {activities.map(a => (
        <button 
          key={a.id} 
          onClick={(e) => { e.stopPropagation(); onSelectActivity(a); }}
          className="absolute transition-all hover:scale-125 z-20 group"
          style={getPos(a.location.lat, a.location.lng)}
        >
          <div className="relative -translate-x-1/2 -translate-y-1/2">
            <div className={`w-12 h-12 rounded-2xl flex items-center justify-center border-4 border-white shadow-xl transition-all ${a.type === 'Culture' ? 'bg-indigo-600' : 'bg-orange-500'}`}>
              <MapIcon size={20} className="text-white" />
              <div className="absolute -top-3 -right-3 bg-slate-900 text-white text-[9px] font-black px-2 py-1 rounded-full shadow-lg border-2 border-white">
                {a.interestedUserIds.length}
              </div>
            </div>
          </div>
        </button>
      ))}

      <div className="absolute bottom-12 left-10 pointer-events-none select-none opacity-40">
        <h3 className="text-3xl font-black text-slate-500 uppercase tracking-tighter italic">Paris Exploration</h3>
        <p className="text-[10px] font-black text-indigo-500 uppercase tracking-[0.4em] mt-1">Live Feed - 50 Active Spots</p>
      </div>
    </div>
  );
};

