          <div className="text-center py-32 bg-white rounded-[40px] border border-[#FEECE0]">
            <MessageCircle size={48} className="mx-auto mb-6 text-slate-100" />
            <p className="text-slate-300 font-black uppercase text-xs tracking-widest">Aucun message</p>
          </div>
        ) : (
          conversations.map(conv => {
            const otherUser = users.find(u => conv.participants.includes(u.id) && u.id !== 'u1');
